def sat(moves: List[List[int]]):
    rods = ([8, 7, 6, 5, 4, 3, 2, 1], [], [])
    for [i, j] in moves:
        rods[j].append(rods[i].pop())
        if not rods[j][-1] == min(rods[j]):
            return False
    return rods[0] == rods[1] == []

def sat(moves: List[List[int]], source, target):
    state = [s[:] for s in source]
    for [i, j] in moves:
        state[j].append(state[i].pop())
        if not state[j] == sorted(state[j]):
            return False
    return state == target

def sat(colors: List[int], n):
    if not (set(colors) <= {0, 1} and len(colors) >= n):
        return False
    squares = {i ** 2: colors[i] for i in range(1, len(colors))}
    return not any((c == d == squares.get(i + j) for (i, c) in squares.items() for (j, d) in squares.items()))

def sat(n: int):
    for i in range(5):
        if not n % 5 == 1:
            return False
        n -= 1 + (n - 1) // 5
    return n > 0 and n % 5 == 1

def sat(coords: List[List[int]], side, num_points):
    for i1 in range(len(coords)):
        (x1, y1) = coords[i1]
        if not (0 <= x1 < side and 0 <= y1 < side):
            return False
        for i2 in range(i1):
            (x2, y2) = coords[i2]
            for i3 in range(i2):
                (x3, y3) = coords[i3]
                if not x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2) != 0:
                    return False
    return len({(a, b) for (a, b) in coords}) == len(coords) >= num_points

def sat(stamps: List[int], target, max_stamps, options):
    for s in stamps:
        if not s in options:
            return False
    return len(stamps) <= max_stamps and sum(stamps) == target

def sat(x: str, puz):
    if not all((c == '_' or c == s for (c, s) in zip(puz, x))):
        return False
    full = set('123456789')
    for i in range(9):
        if not {x[i] for i in range(9 * i, 9 * i + 9)} == full:
            return False
        if not {x[i] for i in range(i, i + 81, 9)} == full:
            return False
        if not {x[9 * a + b + i + 26 * (i % 3)] for a in range(3) for b in range(3)} == full:
            return False
    return True

def sat(xy_sides: List[List[int]]):
    n = max((x + side for (x, y, side) in xy_sides))
    if not len({side for (x, y, side) in xy_sides}) == len(xy_sides) > 1:
        return False
    for (x, y, s) in xy_sides:
        if not (0 <= y < y + s <= n and 0 <= x):
            return False
        for (x2, y2, s2) in xy_sides:
            if not (s2 <= s or x2 >= x + s or x2 + s2 <= x or (y2 >= y + s) or (y2 + s2 <= y)):
                return False
    return sum((side ** 2 for (x, y, side) in xy_sides)) == n ** 2

def sat(n: int, lace):
    sub = lace[n:n + len(lace) // 2]
    return n >= 0 and lace.count('r') == 2 * sub.count('r') and (lace.count('b') == 2 * sub.count('b'))

def sat(n: int):
    s = str(n * n)
    for i in '0123456789':
        if not s.count(i) == 1:
            return False
    return True

def sat(expr: str, nums):
    if not (len(nums) == 4 and 1 <= min(nums) and (max(nums) <= 13)):
        return False
    expr = expr.replace(' ', '')
    digits = ''
    for i in range(len(expr)):
        if i == 0 or expr[i - 1] in '+*-/(':
            if not expr[i] in '123456789(':
                return False
        if not expr[i] in '1234567890()+-*/':
            return False
        digits += expr[i] if expr[i] in '0123456789' else ' '
    if not sorted((int(s) for s in digits.split())) == sorted(nums):
        return False
    return abs(eval(expr) - 24.0) < 1e-06

def sat(moves: List[List[int]], capacities, init, goal):
    state = init.copy()
    for [i, j] in moves:
        if not min(i, j) >= 0:
            return False
        if not i != j:
            return False
        n = min(capacities[j], state[i] + state[j])
        (state[i], state[j]) = (state[i] + state[j] - n, n)
    return state == goal

def sat(li: List[int], words):
    if not (len(li) == len(words) and all((i > 0 and len(str(i)) == len(w) for (i, w) in zip(li, words)))):
        return False
    if not len({c for w in words for c in w}) == len({(d, c) for (i, w) in zip(li, words) for (d, c) in zip(str(i), w)}):
        return False
    return sum(li[:-1]) == li[-1]

def sat(moves: List[int], start):
    locs = {i: [x, y] for (y, row) in enumerate(start) for (x, i) in enumerate(row)}
    for i in moves:
        if not abs(locs[0][0] - locs[i][0]) + abs(locs[0][1] - locs[i][1]) == 1:
            return False
        (locs[0], locs[i]) = (locs[i], locs[0])
    return all((locs[i] == [i % len(start[0]), i // len(start)] for i in locs))

def sat(pair: List[float], nums):
    (a, b) = pair
    if not (a in nums and b in nums and (a != b)):
        return False
    return abs(a - b) == min((x - y for x in nums for y in nums if x > y))

def sat(ls: List[str], combined):
    for s in ls:
        if not s.count('(') == s.count(')'):
            return False
        if not all((s[:i].count('(') > s[:i].count(')') for i in range(1, len(s)))):
            return False
    return ''.join(ls) == combined.replace(' ', '')

def sat(firsts: List[int], balances):
    for (i, bals) in enumerate(balances):
        total = 0
        for b in bals:
            total += b
            if total < 0:
                if not total == firsts[i]:
                    return False
                break
    return True

def sat(depths: List[int], parens):
    groups = parens.split()
    for (depth, group) in zip(depths, groups):
        budget = depth
        success = False
        for c in group:
            if c == '(':
                budget -= 1
                if budget == 0:
                    success = True
                if not budget >= 0:
                    return False
            else:
                if not c == ')':
                    return False
                budget += 1
        if not success:
            return False
    return len(groups) == len(depths)

def sat(containers: List[str], strings, substring):
    i = 0
    for s in strings:
        if substring in s:
            if not containers[i] == s:
                return False
            i += 1
    return i == len(containers)

def sat(nums: List[int], tot, prod):
    if not sum(nums) == tot:
        return False
    p = 1
    for n in nums:
        p *= n
    return p == prod

def sat(maxes: List[int], nums):
    if not len(maxes) == len(nums):
        return False
    for i in range(len(nums)):
        if i > 0:
            if not maxes[i] == max(maxes[i - 1], nums[i]):
                return False
        elif not maxes[0] == nums[0]:
            return False
    return True

def sat(str_num: str, nums):
    (a, b) = nums
    return int(str_num, 2) == int(a, 2) ^ int(b, 2)

def sat(ans: List[int], m, n):
    (gcd, a, b) = ans
    return m % gcd == n % gcd == 0 and a * m + b * n == gcd and (gcd > 0)

def sat(ans: List[str], s, n):
    if not all((ans.count(c.lower()) == 1 for c in s)):
        return False
    if not all((c == c.lower() for c in ans)):
        return False
    if not all((c in s.lower() for c in ans)):
        return False
    return True

def sat(ans: str, s):
    nums = 'zero one two three four five six seven eight nine'.split()
    return [nums.index(x) for x in ans.split(' ')] == sorted([nums.index(x) for x in s.split(' ')])

def sat(inds: List[int], nums):
    (a, b) = inds
    if not (a != b and a >= 0 and (b >= 0)):
        return False
    for i in range(len(nums)):
        for j in range(i):
            if not abs(nums[i] - nums[j]) >= abs(nums[b] - nums[a]):
                return False
    return True

def sat(ans: List[float], nums):
    if not (min(ans) == 0.0 and max(ans) == 1.0):
        return False
    a = min(nums)
    b = max(nums)
    for i in range(len(nums)):
        x = a + (b - a) * ans[i]
        if not abs(nums[i] - x) < 1e-06:
            return False
    return True

def sat(candidates: List[str], int_indices):
    for i in int_indices:
        int(candidates[i])
    for (i, s) in enumerate(candidates):
        if i not in int_indices:
            try:
                int(s)
                return False
            except ValueError:
                pass
    return True

def sat(lengths: List[int], strs):
    for (length, s) in zip(lengths, strs):
        try:
            s[length]
            return False
        except IndexError:
            s[length - 1]
    return len(lengths) == len(strs)

def sat(factors: List[int], n, num_factors):
    if not len(factors) == num_factors:
        return False
    prod = 1
    for d in factors:
        prod *= d
        if not d > 1:
            return False
    return prod == n

def sat(cat: str, strings):
    i = 0
    for s in strings:
        for c in s:
            if not cat[i] == c:
                return False
            i += 1
    return i == len(cat)

def sat(extensions: List[str], strings, prefix):
    i = 0
    for s in strings:
        if s.startswith(prefix):
            if not extensions[i] == s:
                return False
            i += 1
    return i == len(extensions)

def sat(positives: List[int], nums):
    stack = positives[::-1]
    for n in nums:
        if not (n <= 0 or n == stack.pop()):
            return False
    return stack == []

def sat(li: List[int], orig):
    if not orig[::3] == li[::3]:
        return False
    if not sorted(li) == sorted(orig):
        return False
    if not all((li[i] <= li[i + 1] for i in range(1, len(li) - 1, 3))):
        return False
    if not all((li[i] <= li[i + 2] for i in range(2, len(li) - 2, 3))):
        return False
    return True

def sat(li: List[int], orig):
    for i in range(len(li) - 1):
        if not li[i] < li[i + 1]:
            return False
        if not li[i] in orig:
            return False
    for n in orig:
        if not n in li:
            return False
    return True

def sat(li: List[List[int]], n, lower):
    if not len({(i, j) for (i, j) in li}) >= lower:
        return False
    return all((str(i)[j] == '7' and (i % 11 == 0 or i % 13 == 0) and (0 <= i < n) and (0 <= j) for (i, j) in li))

def sat(s: str, target):

    def cycle3(trip):
        return trip if len(trip) != 3 else trip[2] + trip[:2]
    return target == ''.join((cycle3(s[i:i + 3]) for i in range(0, len(s), 3)))

def sat(n: int, lower):
    if not any(((i ** 0.5).is_integer() for i in [5 * n * n - 4, 5 * n * n + 4])):
        return False
    if not all((n % i for i in range(2, int(n ** 0.5) + 1))):
        return False
    return n > lower

def sat(count: int, n):
    for i in range(n):
        for j in range(n):
            count -= 1
    return count == 0

def sat(inds: List[int], nums):
    (a, b) = inds
    return nums[a] + nums[b] == 0 and a >= 0 and (b >= 0)

def sat(init: List[int], target):
    (a, b, c, d) = init
    for i in range(99):
        (a, b, c, d) = (b, c, d, a + b + c + d)
    return a == target

def sat(x: int, nums, upper):
    dev = sum((n - x for n in nums))
    return dev <= upper

def sat(exp_poly: List[int], d, poly):
    p = len(poly)
    if not (p > 2 and all((p % i for i in range(2, p)))):
        return False

    def val(coeffs, n):
        return sum((c * pow(n, i, p) for (i, c) in enumerate(coeffs))) % p
    return all((val(exp_poly, n) == pow(val(poly, n), d, p) for n in range(p)))

def sat(orig: str, result, shift):
    n = len(result)
    if not len(orig) == n:
        return False
    return all((ord(orig[i]) + shift == ord(result[i]) for i in range(n)))

def sat(txt: str, text):
    n = 0
    for c in text:
        if c.lower() not in 'aeiou':
            if not txt[n] == c:
                return False
            n += 1
    if not n == len(txt):
        return False
    return True

def sat(indexes: List[int], nums, thresh):
    j = 0
    for (i, n) in enumerate(nums):
        if n < thresh:
            if not indexes[j] == i:
                return False
            j += 1
    if not j == len(indexes):
        return False
    return True

def sat(matches: List[int], brackets):
    for i in range(len(brackets)):
        j = matches[i]
        c = brackets[i]
        if not (brackets[j] != c and matches[j] == i and all((i < matches[k] < j for k in range(i + 1, j)))):
            return False
    return len(matches) == len(brackets)

def sat(direction: str, nums):
    if direction == 'increasing':
        return all((nums[i] < nums[i + 1] for i in range(len(nums) - 1)))
    if direction == 'decreasing':
        return all((nums[i + 1] < nums[i] for i in range(len(nums) - 1)))

def sat(p: int, n):

    def is_prime(m):
        return all((m % i for i in range(2, m - 1)))
    return is_prime(p) and n % p == 0 and (p > 0) and all((n % i or not is_prime(i) for i in range(p + 1, n)))

def sat(matches: List[int], parens):
    for (i, (j, c)) in enumerate(zip(matches, parens)):
        if not (parens[j] != c and matches[j] == i and all((i < matches[k] < j for k in range(i + 1, j)))):
            return False
    return len(matches) == len(parens)

def sat(derivative: List[int], poly):

    def val(poly, x):
        return sum((coeff * x ** i for (i, coeff) in enumerate(poly)))
    return all((abs(val(poly, x + 1e-08) - val(poly, x) - 1e-08 * val(derivative, x)) < 0.0001 for x in range(len(poly))))

def sat(init: List[int], target):
    (a, b, c) = init
    for i in range(16):
        (a, b, c) = (b, c, a + b + c)
    return a == target

def sat(vowels: List[str], texts):
    for (v, t) in zip(vowels, texts):
        i = 0
        for (j, c) in enumerate(t):
            if c.lower() in 'aeiou' or (c.lower() == 'y' and j == len(t) - 1):
                if not v[i] == c:
                    return False
                i += 1
        if not i == len(v):
            return False
    return len(vowels) == len(texts)

def sat(shifted: str, n, shift):
    if shift > len(str(n)):
        return n == int(shifted[::-1])
    return n == int(shifted[-shift:] + shifted[:-shift])

def sat(tot: int, s):
    for c in s:
        if c.isupper():
            tot -= ord(c)
    return tot == 0

def sat(bananas: int, bowl, total):
    bowl += f' and {bananas} bananas'
    return sum([int(s) for s in bowl.split() if s.isdigit()]) == total

def sat(val_index: List[int], nums):
    if val_index == []:
        return all((n % 2 == 1 for n in nums))
    (v, i) = val_index
    if not (v % 2 == 0 and nums[i] == v):
        return False
    return all((n > v or n % 2 == 1 for n in nums[:i])) and all((n >= v or n % 2 == 1 for n in nums[i:]))

def sat(h: int, seq):
    for i in seq:
        if not not (i > 0 and i > h and (seq.count(i) >= i)):
            return False
    return h == -1 or seq.count(h) >= h > 0

def sat(strange: List[int], li):
    if not sorted(strange) == sorted(li):
        return False
    return all((n == (min, max)[i % 2](strange[i:]) for (i, n) in enumerate(strange)))

def sat(coords: List[List[float]], sides):
    if not len(coords) == 3:
        return False
    sides2 = [((x - x2) ** 2 + (y - y2) ** 2) ** 0.5 for (i, (x, y)) in enumerate(coords) for (x2, y2) in coords[:i]]
    return all((abs(a - b) < 1e-06 for (a, b) in zip(sorted(sides), sorted(sides2))))

def sat(problem: int, weights, max_weight):
    if problem == -1:
        return sum(weights) > max_weight
    return weights[problem] != weights[-1 - problem]

def sat(pal: str, s):
    if not (pal == pal[::-1] and len(pal) == len(s)):
        return False
    return sum((a != b for (a, b) in zip(pal, s))) == sum((a != b for (a, b) in zip(s, s[::-1]))) // 2

def sat(li: List[str], lists):
    width = sum((len(s) for s in li))
    for li2 in lists:
        if not width <= sum((len(s) for s in li2)):
            return False
    return li in lists

def sat(factors: List[List[int]]):
    primes = set(range(2, 1000))
    for n in range(2, 1000):
        if n in primes:
            primes.difference_update(range(2 * n, 1000, n))
    if not all((p in primes for f in factors for p in f)):
        return False
    nums = {p * q * r for (p, q, r) in factors}
    return max(nums) < 1000 and len(nums) == 247

def sat(b: str, n):
    if not b[:4] == b[-4:] == 'bits':
        return False
    inside = b[4:-4]
    if not all((c in '01' for c in inside)):
        return False
    if not (inside[0] == '1' or len(inside) == 1):
        return False
    m = 0
    for c in inside:
        m = 2 * m + int(c)
    return m == n

def sat(indices: List[int], s):
    (i, j) = indices
    return s[i] == s[j] and 0 <= i < j < i + 3

def sat(grades: List[str], gpas):
    if not len(grades) == len(gpas):
        return False
    letters = ['A+', 'A', 'A-', 'B+', 'B', 'B-', 'C+', 'C', 'C-', 'F']
    scores = [4.0, 3.7, 3.4, 3.0, 2.7, 2.4, 2.0, 1.7, 1.4, 0.0]
    for (grade, gpa) in zip(grades, gpas):
        i = letters.index(grade)
        if not gpa >= scores[i]:
            return False
        if not (i == 0 or gpa <= scores[i - 1]):
            return False
    return True

def sat(n: int, b, s):
    n_str = bin(n)[2:]
    return len(n_str) == b and sum((int(i) for i in n_str)) == s

def sat(even_odd_sum: int, nums):
    for i in nums[1::2]:
        if i % 2 == 0:
            even_odd_sum -= i
    return even_odd_sum == 0

def sat(s: str, orig):
    for (a, b) in zip(s.split(' '), orig.split(' ')):
        for i in range(len(a) - 1):
            if not a[i] <= a[i + 1]:
                return False
        if not (len(a) == len(b) and all((a.count(c) == b.count(c) for c in b))):
            return False
    return len(s) == len(orig)

def sat(indices: List[List[int]], uneven, target):
    for (i, j) in indices:
        if not uneven[i][j] == target:
            return False
    for (i, row) in enumerate(uneven):
        for (j, n) in enumerate(row):
            if not (n != target or [i, j] in indices):
                return False
    return True

def sat(up_down: List[int], nums):
    if not all((up_down.count(i) == nums.count(i) for i in set(up_down + nums))):
        return False
    increasing_sign = 1 if (nums[0] + nums[-1]) % 2 == 1 else -1
    return all(((up_down[i + 1] - up_down[i]) * increasing_sign >= 0 for i in range(len(up_down) - 1)))

def sat(encrypted: str, orig):
    if not len(encrypted) == len(orig):
        return False
    return all((chr(ord(a) - 2 * 2) == b for (a, b) in zip(encrypted, orig)))

def sat(n: int, nums):
    if not n in nums:
        return False
    return len({i for i in nums if i <= n}) == 2

def sat(boring: List[str], text):
    sentences = text.replace('!', '.').replace('?', '.').split('.')
    boring_and_exciting = boring + [s for s in sentences if s.split()[:1] != ['I']]
    return sorted(boring_and_exciting) == sorted(sentences)

def sat(s: str, target):
    subs = {ord(c): ord(c) + 2 for c in 'aeiouAEIOU'}
    return s.swapcase() == target.translate(subs)

def sat(ans: List[int], nums):
    (i, digit_sum) = ans
    n = nums[i]

    def is_prime(n):
        return n > 1 and all((n % j for j in range(2, int(n ** 0.5) + 1)))
    return is_prime(n) and all((m <= n for m in nums if is_prime(m))) and (digit_sum == sum((int(c) for c in str(n))))

def sat(primes: List[int], n):
    if not (all((1 < p for p in primes)) and all((p % q for p in primes for q in primes if q < p))):
        return False
    return len({i for p in primes for i in range(p, n, p)}) == max(n - 2, 0)

def sat(prod: int, nums):
    if not all(nums):
        return prod == 0
    for n in nums:
        k = abs(n % 10)
        if k == 0:
            return prod == 0
        if not prod % k == 0:
            return False
        prod //= k
    return prod == 1

def sat(positions: List[int], s):
    if not all((s[i] in 'AEIOU' for i in positions)):
        return False
    return all((i in positions or c not in 'AEIOU' or i % 2 == 1 for (i, c) in enumerate(s)))

def sat(splits: List[List[str]], string):
    (words, separators) = splits
    if not len(words) == len(separators) + 1:
        return False
    merged = []
    for (w, s) in zip(words, separators + [' ']):
        if not s.count(' ') + s.count(',') == len(s) > 0:
            return False
        if not w.count(' ') + w.count(',') == 0:
            return False
        merged += [w, s]
    return ''.join(merged[:-1]) == string

def sat(x: int, a, b):
    if x == -1:
        return all((i % 2 == 1 for i in range(a, b + 1)))
    return a <= x <= b and all((i % 2 == 1 for i in range(x + 1, b + 1)))

def sat(s: str, a, b):
    n = int(s, 2)
    r = range(a, b)
    if len(r) == 0:
        return n == -1
    mu = sum(r) / len(r)
    return abs(mu - n) <= min(abs(mu - n - 1), abs(mu - n + 1))

def sat(sub: List[int], nums):
    for i in range(len(sub)):
        n = sub[i]
        if not n == min(sub[i:]):
            return False
        if not all((int(c) % 2 for c in str(abs(n)))):
            return False
        if not sub.count(n) == nums.count(n):
            return False
    for n in nums:
        if n not in sub:
            if not any((int(c) % 2 == 0 for c in str(abs(n)))):
                return False
    return True

def sat(backwards_digits: List[str], nums):
    digits = {'one': 1, 'two': 2, 'three': 3, 'four': 4, 'five': 5, 'six': 6, 'seven': 7, 'eight': 8, 'nine': 9}
    li = [digits[s] for s in backwards_digits]
    for (i, n) in enumerate(li):
        if not n == max(li[i:i + 2]):
            return False
        if not nums.count(n) == li.count(n):
            return False
    return all((n not in range(1, 10) or n in li for n in nums))

def sat(li: List[int], n):
    if not len(li) == n:
        return False
    for (i, m) in enumerate(li):
        if i < 2:
            if not m == i + 1:
                return False
        elif i % 2 == 1:
            if not m == li[i - 2] + i + (i + 1):
                return False
        elif not m == li[i - 2] * i * (i + 1):
            return False
    return True

def sat(pos: List[int], nums):
    for n in pos + nums:
        s = str(n)
        if int(s[:2]) + sum((int(c) for c in s[2:])) <= 0:
            if not n not in pos:
                return False
        elif not pos.count(n) == nums.count(n):
            return False
    return True

def sat(original: List[int], arr):
    if not str(original)[1:-1] in str(sorted(original) * 2):
        return False
    return any((original == arr[:i] + arr[i + 1:] for i in range(len(arr) + 1)))

def sat(swaps: List[List[int]], nums1, nums2):
    copy1 = nums1[:]
    copy2 = nums2[:]
    for (i, j) in swaps:
        (copy1[i], copy2[j]) = (copy2[j], copy1[i])
    return all((n % 2 == 0 for n in copy1))

def sat(s: str, counts):
    chars = s.split()
    for c in chars:
        if not chars.count(c) == counts[c]:
            return False
    return len(chars) == sum(counts.values())

def sat(strings: List[str], a, b):
    (s, is_palindrome) = strings
    i = 0
    for c in a:
        if c not in b:
            if not s[i] == c:
                return False
            i += 1
    if not i == len(s):
        return False
    return is_palindrome == str(s == s[::-1])

def sat(answers: List[str], lst):
    if len(answers) != len(lst):
        return False
    for (a, s) in zip(answers, lst):
        if 't' in a:
            return False
        num_odds = sum((int(i) % 2 for i in s))
        if a.replace(str(num_odds), 't') != 'this is a test':
            return False
    return True

def sat(start_end: List[int], base, p, upper):
    (start, end) = start_end
    return sum((pow(base, i, p) - p // 2 for i in range(start, end))) <= upper

def sat(wells: List[List[List[int]]], grid, capacity):
    grid2 = [[0 for _ in row] for row in grid]
    for group in wells:
        if not len(group) <= capacity:
            return False
        for (i, j) in group:
            if not grid2[i][j] == 0:
                return False
            grid2[i][j] = 1
    if not sum((len(group) != capacity for group in wells)) <= 1:
        return False
    return grid2 == grid

def sat(ordered: List[int], arr):
    if sorted(ordered) != sorted(arr):
        return False
    return all((bin(a).count('1') <= bin(b).count('1') for (a, b) in zip(ordered, ordered[1:])))

def sat(words: List[str], s, n):
