    for w in s.split():
        num_consonants = 0
        for c in w.lower():
            if c not in 'aeiou':
                num_consonants += 1
        if num_consonants == n:
            if words[i] != w:
                return False
            i += 1
    return i == len(words)

def sat(ham: str, s):
