def sat(s: str, class_name, extensions):
    if not s.startswith(class_name + '.'):
        return False
    ext = s[len(class_name) + 1:]

    def case_delta(x: str):
        tot = 0
        for c in x:
            if c.isupper():
                tot += 1
            elif c.islower():
                tot -= 1
        return tot
    return ext in extensions and case_delta(ext) == max([case_delta(x) for x in extensions])

def sat(n: int, evens, odds):
    for c in str(n):
        if int(c) % 2 == 0:
            evens -= 1
        else:
            odds -= 1
    return evens == 0 and odds == 0

def sat(roman: str, n):
    key = {1000: 'm', 900: 'cm', 500: 'd', 400: 'cd', 100: 'c', 90: 'xc', 50: 'l', 40: 'xl', 10: 'x', 9: 'ix', 5: 'v', 4: 'iv', 1: 'i'}
    m = 0
    for base in [1000, 100, 10, 1]:
        for mul in [9, 4, 5, 1, 1, 1]:
            val = base * mul
            if val in key and roman.startswith(key[val]):
                m += val
                roman = roman[len(key[val]):]
                if mul == 9 or mul == 4:
                    break
    return m == n

def sat(triples: List[List[int]], n, m):
    for (a, b, c) in triples:
        if not (a * a + b * b == c * c and 0 < a < b < c <= n):
