def sat(results: List[List[int]], stats):
    if not len(results) == len(stats):
        return False
    for ((tot, remaining), (eaten, need, stock)) in zip(results, stats):
        if not tot - eaten == min(need, stock):
            return False
        if not (stock < need and remaining == 0 or (stock >= need and remaining + need == stock)):
            return False
    return True

def sat(ops: List[str], target, nums):
    if not (len(ops) == len(set(ops)) and set(ops) == {'**', '*', '+', '-', '//', '%'}):
        return False
    expr = str(nums[0])
    for (n, op) in zip(nums[1:], ops):
        expr += op + str(n)
    return eval(expr) == target

def sat(rev: List[str], strs):
