    for c in s:
        if c in '01234':
            i = '01234'.index(c)
            (matrix[i], matrix[i + 1]) = (matrix[i + 1], matrix[i])
        if c in 'abcde':
            j = 'abcde'.index(c)
            for row in matrix:
                (row[j], row[j + 1]) = (row[j + 1], row[j])
    return len(s) <= max_moves and matrix[2][2] == 1

