def sat(taken: List[int], val_counts, upper):
    advantage = 0
    if not (len(taken) == len(val_counts) and sum(taken) <= upper):
        return False
    for (i, (val, count)) in zip(taken, val_counts):
        if not 0 <= i <= count:
            return False
        advantage += val * i - val * count / 2
    return advantage > 0

