    if not (len(p) == m and len(q) == n):
        return False
    if not (sum(p) == sum(q) == 1.0 and min(p + q) >= 0.0):
        return False
    v = sum((A[i][j] * p[i] * q[j] for i in range(m) for j in range(n)))
    w = sum((B[i][j] * p[i] * q[j] for i in range(m) for j in range(n)))
    return all((sum((A[i][j] * q[j] for j in range(n))) <= v + eps for i in range(m))) and all((sum((B[i][j] * p[i] for i in range(m))) <= w + eps for j in range(n)))

def sat(strategies: List[List[float]], A, eps):
