def sat(indices: List[int], H, alpha, beta, xs, ys, thresh):
    if not sorted({0, len(xs) - 1, *indices}) == indices:
        return False
    cost = alpha * (H - ys[0])
    for (i, j) in zip(indices, indices[1:]):
        (a, b, r) = (xs[i], xs[j], (xs[j] - xs[i]) / 2)
        if not max(ys[i], ys[j]) + r <= H:
            return False
        if not all((ys[k] <= H - r + ((b - xs[k]) * (xs[k] - a)) ** 0.5 for k in range(i + 1, j))):
            return False
        cost += alpha * (H - ys[j]) + beta * (b - a) ** 2
    return cost <= thresh

def sat(position: List[List[int]], transcript):
    board = {(x, y): 0 for x in range(8) for y in range(8) if (x + y) % 2 == 0}
    for (x, y, p) in position:
        if not (-2 <= p <= 2 and board[x, y] == 0):
            return False
        board[x, y] = p

    def has_a_jump(x, y):
        p = board[x, y]
        deltas = [(dx, dy) for dx in [-1, 1] for dy in [-1, 1] if dy != -p]
        return any((board.get((x + 2 * dx, y + 2 * dy)) == 0 and board[x + dx, y + dy] * p < 0 for (dx, dy) in deltas))
    sign = 1
    for move in transcript:
        (start, end) = (tuple(move[0]), tuple(move[-1]))
        p = board[start]
        if not p * sign > 0:
            return False
        if not all((board[x, y] == 0 for (x, y) in move if [x, y] != move[0])):
            return False
        for ((x1, y1), (x2, y2)) in zip(move, move[1:]):
            if not (abs(p) != 1 or (y2 - y1) * p > 0):
                return False
            if abs(x2 - x1) == 1:
                if not not any((has_a_jump(*a) for a in board if board[a] * p > 0)):
                    return False
                break
            mid = ((x1 + x2) // 2, (y1 + y2) // 2)
            if not board[mid] * p < 0:
                return False
            board[mid] = 0
        (board[start], board[end]) = (0, p)
        if not (abs(x2 - x1) == 1 or not has_a_jump(*end)):
            return False
        if abs(p) == 1 and any((y in {0, 7} for (x, y) in move[1:])):
            board[end] *= 2
        sign *= -1
    return True

def sat(cut_position: int, ring, lower):
    line = ring[cut_position:] + ring[:cut_position]
    matches = {c: 0 for c in line.lower()}
    for c in line:
        if c.islower():
            matches[c] -= 1 if matches[c] > 0 else len(line)
        else:
            matches[c.lower()] += 1
    return sum((i == 0 for i in matches.values())) >= lower

