        return False
    for (prev, cur) in zip(states, states[1:]):
        for i in range(5):
            if cur[i] != prev[i]:
                break
        if not cur[i] < prev[i]:
            return False
        if not (cur[i + 1] - prev[i + 1] == 2 * (prev[i] - cur[i]) and cur[i + 2:] == prev[i + 2:] or (cur[i:i + 3] == [prev[i] - 1, prev[i + 2], prev[i + 1]] and cur[i + 3:] == prev[i + 3:])):
            return False
    return states[-1][-1] == 2 ** n

def sat(nums: List[int], b, m):
