        return False

    def gcd(i, j):
        (r, s) = (max(i, j), min(i, j))
        while s >= 1:
            (r, s) = (s, r % s)
        return r
    for a in nums:
        nums = [(a + i + 1) ** 2 + (a + i + 1) + 1 for i in range(b)]
        if not all((any((i != j and gcd(i, j) > 1 for j in nums)) for i in nums)):
            return False
    return True

