def sat(counts: List[int], target_prob):
    (m, n) = counts
    probs = [1.0] + [0.0] * n
    for i in range(2, m + 1):
        old_probs = probs
        probs = [1.0] + [0.0] * n
        for j in range(1, min(n + 1, i)):
            probs[j] = j / (i + j) * probs[j - 1] + i / (i + j) * old_probs[j]
    return abs(probs[n] - target_prob) < 1e-06

